mapper.select:
select b.PassDate, b.PhoneNumber,b.ParkName, b.PassTrueName,b.DoorName,b.Temperature,b.IsIn,b.Admit,a.company,a.department from
(SELECT t2.PhoneNumber,t1.ParkName,t1.epidemic_id, t1.PassTrueName,t1.PassDate,t1.DoorName,t1.Temperature,t1.IsIn,t1.Admit FROM biz_pass_record as t1 left join
sys_user t2 on t1.PassUserId = t2.id where t2.PhoneNumber is not NULL  and t2.PhoneNumber!='undefined') b left JOIN biz_epidemic a
on a.id = b.epidemic_id


        AND b.PassTrueName = #{PassTrueName}


        AND  a.company LIKE CONCAT('%', #{company,jdbcType=VARCHAR}, '%')


        AND  a.department LIKE CONCAT('%', #{department,jdbcType=VARCHAR}, '%')


        and b.PassDate  >=  #{startTime} and  b.PassDate  <=  #{endTime}


order by b.PassDate DESC
LIMIT #{start},#{limit}

select DISTINCT b.PassDate, b.PhoneNumber,b.ParkName, b.PassTrueName,b.DoorName,b.Temperature,b.IsIn,b.Admit,a.company,a.department from (SELECT DISTINCT t2.PhoneNumber,t1.ParkName,t1.epidemic_id, t1.PassTrueName,t1.PassDate,t1.DoorName,t1.Temperature,t1.IsIn,t1.Admit FROM biz_pass_record as t1 left join sys_user t2 on t1.PassUserId = t2.id where t2.PhoneNumber is not NULL and t2.PhoneNumber!='undefined') b left JOIN biz_epidemic a on a.id = b.epidemic_id
AND b.PassTrueName = #{PassTrueName}
and b.PassDate >= #{startTime} and b.PassDate <= #{endTime}
order by b.PassDate DESC
